import React, { useState } from 'react';
import LoginBg from '../login1.png';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './index.css';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser = async (e) => {
    e.preventDefault();
    const res = await fetch('/logins', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email,
        password,
      }),
    });

    const data = await res.json();
    if (res.status === 422 || !data) {
      toast.error('Invalid credentials');
    } else if (!email || !password) {
      toast.error('Please fill in all fields');
    } else {
      toast.success('Login successful');
      navigate('/main2');
    }
  };

  // Check if user is already authenticated and redirect to /main2
  // if necessary
  const isAuthenticated = true; // Replace with your actual authentication check
  if (isAuthenticated) {
    navigate('/main2');
  }

  return (
    <>
      <div
        className="bg"
        style={{
          backgroundImage: `linear-gradient(to bottom, rgba(150, 0, 250, 0.1), rgba(0, 19, 100, 0)), url(${LoginBg}) `,
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          width: '100%',
        }}
      >
        <div className="Auth-form-container">
          <form method="POST" className="Auth-form">
            <div className="Auth-form-content">
              <h2 className="Auth-form-title">Sign In</h2>
              <div className="form-group mt-3">
                <input
                  type="email"
                  className="form-control mt-1"
                  name="email"
                  id="email"
                  autoComplete="on"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Your Email*"
                />
              </div>
              <div className="form-group mt-3">
                <input
                  type="password"
                  className="form-control mt-1"
                  name="password"
                  id="password"
                  autoComplete="on"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Your Password*"
                />
              </div>
              <div className="d-grid gap-2 mt-3">
                <input
                  type="submit"
                  value="Sign In"
                  className="btn"
                  name="signin"
                  onClick={loginUser}
                />
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
