import React, { useState } from 'react';
import LoginBg from '../login1.png';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './index.css';



const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser = async (e) => {
    e.preventDefault();
    const res = await fetch('/logins', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email,
        password,
      }),
    });

    const data = res.json();
    if (res.status === 422 || !data) {
      toast.error('Invalid credentials');
    } else if (!email || !password) {
      toast.error('Please fill in all fields');
    } else {
      toast.success('Login successful');
      navigate('/main2');
    }
  };

  // Check if user is already logged in
  const isLoggedIn = true; // Replace this with your actual authentication check

  // Redirect to main2 if user is already logged in
  if (isLoggedIn) {
    navigate('/main2');
    return null; // Render nothing while redirecting
  }

  return (
    <>
        <div className="Auth-form-container">
          <form method="POST" className="Auth-form">
            <div className="Auth-form-content">
              <h2 className="Auth-form-title">Sign In</h2>
              <div className="form-group mt-3">
                {/* <label>Email address</label>
                                <input
                                    type="email" value={email} onChange={(e)=>setEmail(e.target.value)}
                                    className="form-control mt-1" name='email'
                                    placeholder="Enter email"
                                /> */}

                <input
                  type="email"
                  className="form-control mt-1"
                  name="email"
                  id="email"
                  autoComplete="on"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Your Email*"
                ></input>
              </div>
              <div className="form-group mt-3">
                {/* <label>Password</label>
                                <input
                                    type="password" value={password} onChange={(e)=>setPassword(e.target.value)}
                                    className="form-control mt-1"   name='password'
                                    placeholder="Enter password"
                                /> */}
                <input
                  type="password"
                  className="form-control mt-1"
                  name="password"
                  id="password"
                  autoComplete="on"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Your Password*"
                ></input>
              </div>
              <div className="d-grid gap-2 mt-3">
                <input
                  type="submit"
                  value="signin"
                  className="btn"
                  name="signin"
                  onClick={loginUser}
                ></input>
              </div>
            </div>
          </form>
        </div>
    </>
  );
};

export default Login;
